import streamlit as st
import pandas as pd
import numpy as np

#Displaying text Content
st.title("My First STREAMLIT App")
st.write("Hello Ishika")
st.text("Lets start")

#Creating Charts using Pandas & Numpy
df=pd.DataFrame(np.random.randn(10,2),columns=['A','B'])
st.line_chart(df)
st.bar_chart(df)

#sidebar,Image and Video
st.sidebar.title("Navigation")
st.image("data:image/jpeg;base64,")
st.video("https://www.youtube.com/watch?v=8DIv6eU5kM8")

#File Upload (csv)
upload_file=st.file_uploader("Upload a CSV file", type='csv')
if upload_file:
    df=pd.read_csv(upload_file)
    st.dataframe(df)

#Taking User Inputs
name=st.text_input("Enter your name: ")
if st.button("Welcome"):
    st.success(f"Hello, {name}")

#Text & Markdown Formatting
st.header("This is a header")
st.subheader("This is a subheader")

st.markdown("**Bold**,**Italic**,'Code',[Link](https://streamlit.io)")
st.code("for i in range(5): print(i)", language="python")

st.text_input("What is your Name:")
st.text_area("write something....")
st.number_input("pick a number", min_value=0,max_value=100)
st.slider("Choose a range",0,100)
st.selectbox("Select a fruit",["Apple","Banana","Mango"])
st.multiselect("Choose Toppings:",["Apple","Banana","Carrot"])
st.radio("Pick one",["Option A","Option B"])
st.checkbox("I agree to the terms")

#Matplotlib Integration

import matplotlib.pyplot as plt
fig,ax=plt.subplots()
ax.plot([1,2,3],[4,5,6])
st.pyplot(fig)
